import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import DescriptionIcon from "@material-ui/icons/Description";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import MailIcon from "@material-ui/icons/Mail";
import MenuIcon from "@material-ui/icons/Menu";
import PersonIcon from "@material-ui/icons/Person";
import ComputerIcon from "@material-ui/icons/Computer";
import LinkedInIcon from "@material-ui/icons/LinkedIn";
import GitHubIcon from "@material-ui/icons/GitHub";
import HomeIcon from "@material-ui/icons/Home";
import AccountTreeIcon from "@material-ui/icons/AccountTree";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { Link } from "react-router-dom";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Skills from "./Skills";
import Projects from "./projects/Projects";
import ProfileImageCard from "../ProfileImageCard"

const { uuid } = require('uuidv4');
const drawerWidth = 210;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up("sm")]: {
      width: `calc(100% - ${drawerWidth}px)`,
      height: "75px",
      marginLeft: drawerWidth,
      background: 'primary'
    },
  },
  name: {
    color: "black",
    alignItems: "center"
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  card: {
    marginBottom: "20px",
  },
  a: {
    color: "inherit",
    textDecoration: "none"
  }
}));

function Menu(props) {
  const { window } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const menuItemTop = [
    {
      text: "Home",
      icon: <HomeIcon />,
      link: "/",
    },
    {
      text: "About Me",
      icon: <PersonIcon />,
      link: "/about",
    },
    {
      text: "Skills",
      icon: <ComputerIcon />,
      link: "/skills",
    },
    {
      text: "Projects",
      icon: <AccountTreeIcon />,
      link: "projects",
    },
    {
      text: "Contact Me",
      icon: <MailIcon />,
      link: "/contact",
    },
  ];

  const menuItemBottom = [
    {
      text: "Github",
      icon: <GitHubIcon />,
      link: "https://github.com/guidoasbun",
    },
    {
      text: "LinkedIn",
      icon: <LinkedInIcon />,
      link: "https://www.linkedin.com/in/guidoasbun/",
    },
    {
      text: "Resume",
      icon: <DescriptionIcon />,
      link: "https://guido-asbun.s3.amazonaws.com/Snap+Resume+1.pdf",
    },
  ];

  const drawer = (
    <div>
      <ProfileImageCard />
      <Divider />
      <List>
        {menuItemTop.map((item, index) => (
          <Link className={classes.a} to={item.link} key={uuid()}>
            <ListItem button >
              <ListItemText primary={item.text} />
              <ListItemIcon>{item.icon}</ListItemIcon>
            </ListItem>
          </Link>
        ))}
      </List>
      <Divider />
      <List>
        {menuItemBottom.map((item, index) => (
          <a target="_blank" href={item.link} className={classes.a} key={uuid()}>
            <ListItem button >
              <ListItemText primary={item.text} />
              <ListItemIcon>{item.icon}</ListItemIcon>
            </ListItem>
          </a>
        ))}
      </List>
      <Divider />
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root} variant="h2">
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Typography variant="h2" className={classes.name} align="center">
          Guido Asbun
        </Typography>

        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === "rtl" ? "right" : "left"}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>
        <div className={classes.toolbar} />
      </main>
    </div>
  );
}

Menu.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default Menu;


